import React, { useState, useEffect } from 'react';

function Servers({ isAuthenticated }) {
  const [servers, setServers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);
  const [sortOrder, setSortOrder] = useState('asc');
  const [filter, setFilter] = useState('all');
  const [viewMode, setViewMode] = useState('list');
  const [editingServer, setEditingServer] = useState(null);
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    if (isAuthenticated) {
      fetchServers();
    } else {
      setLoading(false);
    }
  }, [isAuthenticated]);

  const fetchServers = async () => {
    try {
      const response = await fetch('/api/servers');
      if (!response.ok) throw new Error('Failed to fetch servers');
      const data = await response.json();
      setServers(data);
    } catch (error) {
      console.error('Error fetching servers:', error);
      setError('Error fetching servers: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSort = () => {
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
  };

  const handleFilter = (event) => {
    setFilter(event.target.value);
  };

  const handleViewMode = (mode) => {
    setViewMode(mode);
  };

  const handleEdit = (server) => {
    setEditingServer(server);
  };

  const handleSave = (server) => {
    // Implement save logic here
    setNotifications([...notifications, 'Server settings saved successfully!']);
    setEditingServer(null);
  };

  const handleExport = () => {
    const csvContent = "data:text/csv;charset=utf-8," 
      + servers.map(server => `${server.name},${server.id},${server.memberCount},${server.ownerID}`).join("\n");
    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.setAttribute("href", encodedUri);
    link.setAttribute("download", "servers.csv");
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const filteredServers = servers
    .filter(server =>
      server.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (filter === 'all' || (filter === 'withBot' && server.hasBot) || (filter === 'withLicense' && server.hasLicense))
    )
    .sort((a, b) => {
      if (sortOrder === 'asc') return a.name.localeCompare(b.name);
      return b.name.localeCompare(a.name);
    });

  const indexOfLastServer = currentPage * itemsPerPage;
  const indexOfFirstServer = indexOfLastServer - itemsPerPage;
  const currentServers = filteredServers.slice(indexOfFirstServer, indexOfLastServer);

  const totalPages = Math.ceil(filteredServers.length / itemsPerPage);

  if (!isAuthenticated) {
    return <div className="page"><p>Proszę się zalogować, aby uzyskać dostęp do tej strony.</p></div>;
  }

  if (loading) {
    return <div className="page"><p>Loading...</p></div>;
  }

  if (error) {
    return <div className="page"><p>{error}</p></div>;
  }

  return (
    <div className="page">
      <h1>Servers</h1>
      <input
        type="text"
        placeholder="Search servers"
        value={searchTerm}
        onChange={handleSearch}
      />
      <button onClick={handleSort}>Sort {sortOrder === 'asc' ? 'Descending' : 'Ascending'}</button>
      <select value={filter} onChange={handleFilter}>
        <option value="all">All</option>
        <option value="withBot">With Bot</option>
        <option value="withLicense">With License</option>
      </select>
      <button onClick={() => handleViewMode('list')}>List View</button>
      <button onClick={() => handleViewMode('grid')}>Grid View</button>
      <button onClick={handleExport}>Export to CSV</button>
      {notifications.map((note, index) => (
        <p key={index} className="notification">{note}</p>
      ))}
      <div className={viewMode === 'list' ? 'list-view' : 'grid-view'}>
        {currentServers.map((server) => (
          <div key={server.id} className="server-item">
            <img src={`https://cdn.discordapp.com/icons/${server.id}/${server.icon}.png`} alt={`${server.name} icon`} width="50" height="50" />
            <div className="server-info">
              {editingServer && editingServer.id === server.id ? (
                <>
                  <input type="text" value={editingServer.name} onChange={(e) => setEditingServer({ ...editingServer, name: e.target.value })} />
                  <button onClick={() => handleSave(editingServer)}>Save</button>
                </>
              ) : (
                <>
                  <h2>{server.name}</h2>
                  <p>Members: {server.memberCount}</p>
                  <p>Owner ID: {server.ownerID}</p>
                  <button onClick={() => handleEdit(server)}>Edit</button>
                </>
              )}
              {server.hasLicense && server.hasBot ? (
                <button onClick={() => window.location.href = `/dashboard/${server.id}`}>Dashboard</button>
              ) : server.hasLicense && !server.hasBot ? (
                <button onClick={() => window.location.href = `https://discord.com/oauth2/authorize?client_id=${process.env.DISCORD_CLIENT_ID}&scope=bot&permissions=8&guild_id=${server.id}`}>Invite Bot</button>
              ) : (
                <a href="/license">Activate License</a>
              )}
            </div>
          </div>
        ))}
      </div>
      <div className="pagination">
        {[...Array(totalPages)].map((_, index) => (
          <button key={index} onClick={() => setCurrentPage(index + 1)}>{index + 1}</button>
        ))}
      </div>
    </div>
  );
}

export default Servers;
