import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import LeftSidebar from '../components/LeftSidebar';

function Dashboard() {
  const { id } = useParams();
  const [server, setServer] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState('');
  const [embedTitle, setEmbedTitle] = useState('');
  const [embedColor, setEmbedColor] = useState('#000000');
  const [embedDescription, setEmbedDescription] = useState('');
  const [embedFooter, setEmbedFooter] = useState('');
  const [menuType, setMenuType] = useState('normal');
  const [menuItems, setMenuItems] = useState([]);
  const [menuItemLabel, setMenuItemLabel] = useState('');
  const [menuItemMessage, setMenuItemMessage] = useState('');
  const [channels, setChannels] = useState([]);
  const [selectedChannel, setSelectedChannel] = useState('');
  const [messageTemplates, setMessageTemplates] = useState([]);
  const [embedTemplates, setEmbedTemplates] = useState([]);
  const [preview, setPreview] = useState(false);
  const [attachments, setAttachments] = useState([]);
  const [history, setHistory] = useState([]);

  useEffect(() => {
    fetchServerDetails();
    fetchChannels();
  }, [id]);

  const fetchServerDetails = async () => {
    try {
      const response = await fetch(`/api/server/${id}`);
      if (!response.ok) throw new Error('Failed to fetch server details');
      const data = await response.json();
      setServer(data);
    } catch (error) {
      console.error('Error fetching server details:', error);
      setError('Error fetching server details: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchChannels = async () => {
    try {
      const response = await fetch(`/api/server/${id}/channels`);
      if (!response.ok) throw new Error('Failed to fetch channels');
      const data = await response.json();
      setChannels(data);
      if (data.length > 0) {
        setSelectedChannel(data[0].id);
      }
    } catch (error) {
      console.error('Error fetching channels:', error);
      setError('Error fetching channels: ' + error.message);
    }
  };

  const handleAddMenuItem = () => {
    setMenuItems([...menuItems, { label: menuItemLabel, message: menuItemMessage }]);
    setMenuItemLabel('');
    setMenuItemMessage('');
  };

  const handleSendMessage = async () => {
    try {
      const embedMessage = {
        title: embedTitle,
        color: embedColor,
        description: embedDescription,
        footer: embedFooter,
      };

      const response = await fetch(`/api/server/${id}/message`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          channelId: selectedChannel,
          message,
          embedMessage,
          menuType,
          menuItems,
          attachments
        }),
      });
      if (!response.ok) throw new Error('Failed to send message');
      alert('Message sent successfully');
      setHistory([...history, { message, embedMessage, menuType, menuItems }]);
    } catch (error) {
      console.error('Error sending message:', error);
      setError('Error sending message: ' + error.message);
    }
  };

  const handleSaveMessageTemplate = () => {
    setMessageTemplates([...messageTemplates, message]);
    setMessage('');
  };

  const handleSaveEmbedTemplate = () => {
    const embedMessage = {
      title: embedTitle,
      color: embedColor,
      description: embedDescription,
      footer: embedFooter,
    };
    setEmbedTemplates([...embedTemplates, embedMessage]);
    setEmbedTitle('');
    setEmbedColor('#000000');
    setEmbedDescription('');
    setEmbedFooter('');
  };

  const handlePreviewToggle = () => {
    setPreview(!preview);
  };

  const handleFileUpload = (e) => {
    setAttachments([...attachments, ...e.target.files]);
  };

  if (loading) {
    return <div className="page"><p>Loading...</p></div>;
  }

  if (error) {
    return <div className="page"><p>{error}</p></div>;
  }

  return (
    <div className="container">
      <LeftSidebar />
      <div className="page">
        {server ? (
          <>
            <h1>{server.name}</h1>
            <img src={server.icon} alt={`${server.name} icon`} width="100" height="100" />
            <p>Members: {server.memberCount}</p>
            <p>Owner ID: {server.ownerID}</p>
            {server.license ? (
              <>
                <p>License Key: {server.license.key}</p>
                <p>Expires At: {new Date(server.license.expiresAt).toLocaleString()}</p>
              </>
            ) : (
              <p>No active license</p>
            )}
            <div className="section">
              <h2 onClick={() => document.getElementById('messagesSection').classList.toggle('hidden')}>Messages</h2>
              <div id="messagesSection" className="hidden">
                <label>
                  Select Channel:
                  <select value={selectedChannel} onChange={(e) => setSelectedChannel(e.target.value)}>
                    {channels.map(channel => (
                      <option key={channel.id} value={channel.id}>{channel.name}</option>
                    ))}
                  </select>
                </label>
                <h2>Create Message</h2>
                <textarea
                  value={message}
                  onChange={(e) => setMessage(`**${message}** ${e.target.value}`)}
                  placeholder="Enter your message"
                />
                <h2>Create Embed Message</h2>
                <label>
                  Title:
                  <input
                    type="text"
                    value={embedTitle}
                    onChange={(e) => setEmbedTitle(e.target.value)}
                  />
                </label>
                <label>
                  Color:
                  <input
                    type="color"
                    value={embedColor}
                    onChange={(e) => setEmbedColor(e.target.value)}
                  />
                </label>
                <textarea
                  value={embedDescription}
                  onChange={(e) => setEmbedDescription(e.target.value)}
                  placeholder="Enter your embed description"
                />
                <label>
                  Footer:
                  <input
                    type="text"
                    value={embedFooter}
                    onChange={(e) => setEmbedFooter(e.target.value)}
                  />
                </label>
                <div>
                  <label>
                    Attach Files:
                    <input type="file" multiple onChange={handleFileUpload} />
                  </label>
                </div>
                <button onClick={handlePreviewToggle}>Preview Message</button>
                <button onClick={handleSendMessage}>Send Message</button>
                <button onClick={handleSaveMessageTemplate}>Save as Template</button>
                <button onClick={handleSaveEmbedTemplate}>Save Embed as Template</button>
                {preview && (
                  <div className="preview">
                    <h3>Message Preview:</h3>
                    <p>{message}</p>
                    <h3>Embed Preview:</h3>
                    <div>
                      <h4 style={{ color: embedColor }}>{embedTitle}</h4>
                      <p>{embedDescription}</p>
                      <p>{embedFooter}</p>
                    </div>
                  </div>
                )}
              </div>
            </div>
            <div className="section">
              <h2 onClick={() => document.getElementById('menuSection').classList.toggle('hidden')}>Create Menu</h2>
              <div id="menuSection" className="hidden">
                <label>
                  Menu Type:
                  <select value={menuType} onChange={(e) => setMenuType(e.target.value)}>
                    <option value="normal">Normal</option>
                    <option value="dropdown">Dropdown</option>
                  </select>
                </label>
                <div>
                  <input
                    type="text"
                    value={menuItemLabel}
                    onChange={(e) => setMenuItemLabel(e.target.value)}
                    placeholder="Menu Item Label"
                  />
                  <input
                    type="text"
                    value={menuItemMessage}
                    onChange={(e) => setMenuItemMessage(e.target.value)}
                    placeholder="Menu Item Message"
                  />
                  <button onClick={handleAddMenuItem}>Add Menu Item</button>
                </div>
                <ul>
                  {menuItems.map((item, index) => (
                    <li key={index}>{item.label} - {item.message}</li>
                  ))}
                </ul>
              </div>
            </div>
            <div className="section">
              <h2 onClick={() => document.getElementById('historySection').classList.toggle('hidden')}>Message History</h2>
              <div id="historySection" className="hidden">
                  <ul>
                    {history.map((item, index) => (
                      <li key={index}>
                        <p><strong>Message:</strong> {item.message}</p>
                        <p><strong>Embed:</strong> {item.embedMessage && item.embedMessage.title ? item.embedMessage.title : 'No embed'}</p>
                        <p><strong>Channel:</strong> {channels.find(channel => channel.id === selectedChannel)?.name || 'Unknown'}</p>
                        <p><strong>Menu Type:</strong> {item.menuType}</p>
                        <p><strong>Menu Items:</strong> {item.menuItems.map(menuItem => menuItem.label).join(', ')}</p>
                      </li>
                    ))}
                  </ul>
              </div>
            </div>
            <div className="section">
              <h2 onClick={() => document.getElementById('rolesSection').classList.toggle('hidden')}>Roles Management</h2>
              <div id="rolesSection" className="hidden">
                <p>Here you can manage server roles...</p>
              </div>
            </div>
            <div className="section">
              <h2 onClick={() => document.getElementById('logsSection').classList.toggle('hidden')}>Activity Logs</h2>
              <div id="logsSection" className="hidden">
                <p>Here you can see server activity logs...</p>
              </div>
            </div>
            <div className="section">
              <h2 onClick={() => document.getElementById('membersSection').classList.toggle('hidden')}>Member Management</h2>
              <div id="membersSection" className="hidden">
                <p>Here you can manage server members...</p>
              </div>
            </div>
            <div className="section">
              <h2 onClick={() => document.getElementById('statisticsSection').classList.toggle('hidden')}>Server Statistics</h2>
              <div id="statisticsSection" className="hidden">
                <p>Here you can see server statistics...</p>
              </div>
            </div>
            <div className="section">
              <h2 onClick={() => document.getElementById('notificationsSection').classList.toggle('hidden')}>Notifications</h2>
              <div id="notificationsSection" className="hidden">
                <p>Here you can set up server notifications...</p>
              </div>
            </div>
            <div className="section">
              <h2 onClick={() => document.getElementById('channelManagementSection').classList.toggle('hidden')}>Channel Management</h2>
              <div id="channelManagementSection" className="hidden">
                <p>Here you can manage server channels...</p>
              </div>
            </div>
            <div className="section">
              <h2 onClick={() => document.getElementById('automationsSection').classList.toggle('hidden')}>Automations</h2>
              <div id="automationsSection" className="hidden">
                <p>Here you can set up server automations...</p>
              </div>
            </div>
            <div className="section">
              <h2 onClick={() => document.getElementById('invitationsSection').classList.toggle('hidden')}>Invitations</h2>
              <div id="invitationsSection" className="hidden">
                <p>Here you can manage server invitations...</p>
              </div>
            </div>
            <div className="section">
              <h2 onClick={() => document.getElementById('licenseStatisticsSection').classList.toggle('hidden')}>License Statistics</h2>
              <div id="licenseStatisticsSection" className="hidden">
                <p>Here you can see detailed license statistics...</p>
              </div>
            </div>
          </>
        ) : (
          <p>Server not found</p>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
