import React, { useState, useEffect } from 'react';

function Servers({ isAuthenticated }) {
  const [servers, setServers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (isAuthenticated) {
      fetchServers();
    } else {
      setLoading(false);
    }
  }, [isAuthenticated]);

  const fetchServers = async () => {
    try {
      const response = await fetch('/api/servers');
      if (!response.ok) throw new Error('Failed to fetch servers');
      const data = await response.json();
      setServers(data);
    } catch (error) {
      console.error('Error fetching servers:', error);
      setError('Error fetching servers');
    } finally {
      setLoading(false);
    }
  };

  if (!isAuthenticated) {
    return <div className="page"><p>Proszę się zalogować, aby uzyskać dostęp do tej strony.</p></div>;
  }

  if (loading) {
    return <div className="page"><p>Loading...</p></div>;
  }

  if (error) {
    return <div className="page"><p>{error}</p></div>;
  }

  return (
    <div className="page">
      <h1>Servers</h1>
      <ul>
        {servers.map((server) => (
          <li key={server.id}>
            <img src={`https://cdn.discordapp.com/icons/${server.id}/${server.icon}.png`} alt={`${server.name} icon`} width="50" height="50" />
            {server.name}
            {(server.permissions & 0x8) === 0x8 ? (
              <span> - You are an admin on this server</span>
            ) : (
              <span> - You are not an admin on this server</span>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Servers;
