import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import LeftSidebar from './components/LeftSidebar';
import Home from './pages/Home';
import License from './pages/License';
import Servers from './pages/Servers';
import Dashboard from './pages/Dashboard';
import Logout from './pages/Logout';
import ServerSettings from './pages/ServerSettings';
import BotSettings from './pages/BotSettings';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');

  useEffect(() => {
    fetch('/auth/check')
      .then(res => res.json())
      .then(data => {
        setIsAuthenticated(data.isAuthenticated);
        setUsername(data.username);
      })
      .catch(err => console.error('Error checking authentication status:', err));
  }, []);

  return (
    <Router>
      <div className="App">
        <Navbar isAuthenticated={isAuthenticated} />
        <Switch>
          <Route path="/" exact>
            <Home isAuthenticated={isAuthenticated} username={username} />
          </Route>
          <Route path="/license">
            <License isAuthenticated={isAuthenticated} />
          </Route>
          <Route path="/servers">
            <Servers isAuthenticated={isAuthenticated} />
          </Route>
          <Route path="/dashboard/:id">
            <Dashboard />
          </Route>
          <Route path="/logout">
            <Logout />
          </Route>
          <Route path="/dashboard/:id" exact component={Dashboard} />
          <Route path="/dashboard/:id/serversettings" component={ServerSettings} />
          <Route path="/dashboard/:id/botsettings" component={BotSettings} />
        </Switch>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
