import React, { useState, useEffect } from 'react';

function License({ isAuthenticated }) {
  const [servers, setServers] = useState([]);
  const [licenseKey, setLicenseKey] = useState('');
  const [selectedServer, setSelectedServer] = useState('');

  useEffect(() => {
    if (isAuthenticated) {
      fetchServers();
      fetchLicenses();
    }
  }, [isAuthenticated]);

  const fetchServers = async () => {
    try {
      const response = await fetch('/api/servers');
      const data = await response.json();
      setServers(data);
    } catch (error) {
      console.error('Error fetching servers:', error);
    }
  };

  const fetchLicenses = async () => {
    try {
      const response = await fetch('/api/licenses');
      const licenses = await response.json();
      setServers(prevServers =>
        prevServers.map(server => {
          const license = licenses.find(lic => lic.serverId === server.id);
          if (license) {
            const daysLeft = Math.ceil(
              (new Date(license.expiresAt) - new Date()) / (1000 * 60 * 60 * 24)
            );
            return {
              ...server,
              licenseExpires: license.expiresAt,
              daysLeft: daysLeft,
            };
          }
          return server;
        })
      );
    } catch (error) {
      console.error('Error fetching licenses:', error);
    }
  };

  const handleActivateLicense = async (event) => {
    event.preventDefault();
    if (licenseKey && selectedServer) {
      try {
        const response = await fetch('/api/licenses', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            key: licenseKey,
            serverId: selectedServer,
          }),
        });
        if (response.ok) {
          const newLicense = await response.json();
          setServers(prevServers =>
            prevServers.map(server =>
              server.id === newLicense.serverId
                ? {
                    ...server,
                    licenseExpires: newLicense.expiresAt,
                    daysLeft: Math.ceil(
                      (new Date(newLicense.expiresAt) - new Date()) /
                      (1000 * 60 * 60 * 24)
                    ),
                  }
                : server
            )
          );
        } else {
          alert('Failed to activate license.');
        }
      } catch (error) {
        console.error('Error activating license:', error);
        alert('An error occurred while activating the license.');
      }
    } else {
      alert('Please enter a license key and select a server.');
    }
  };

  if (!isAuthenticated) {
    return <div className="page"><p>Proszę się zalogować, aby uzyskać dostęp do tej strony.</p></div>;
  }

  return (
    <div className="page">
      <h1>License</h1>
      <form onSubmit={handleActivateLicense}>
        <label>
          License Key:
          <input
            type="text"
            value={licenseKey}
            onChange={(e) => setLicenseKey(e.target.value)}
          />
        </label>
        <label>
          Select Server:
          <select
            value={selectedServer}
            onChange={(e) => setSelectedServer(e.target.value)}
          >
            <option value="">Select a server</option>
            {servers.map((server) => (
              <option key={server.id} value={server.id}>
                {server.name}
              </option>
            ))}
          </select>
        </label>
        <button type="submit">Activate</button>
      </form>
      <h2>Servers</h2>
      <ul>
        {servers.map((server) => (
          <li key={server.id}>
            <img src={`https://cdn.discordapp.com/icons/${server.id}/${server.icon}.png`} alt={`${server.name} icon`} width="50" height="50" />
            {server.name}
            {server.licenseExpires ? (
              <span> - License expires on {server.licenseExpires} ({server.daysLeft} days left)</span>
            ) : (
              <span> - No license</span>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default License;
