import React, { useState, useEffect } from 'react';

function Menu({ serverId, channels, selectedChannel, setSelectedChannel }) {
  const [messages, setMessages] = useState([]);
  const [selectedMessage, setSelectedMessage] = useState('');
  const [menuType, setMenuType] = useState('normal');
  const [menuItems, setMenuItems] = useState([]);
  const [menuItemLabel, setMenuItemLabel] = useState('');
  const [menuItemMessage, setMenuItemMessage] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    if (selectedChannel) {
      fetchMessages(selectedChannel);
    }
  }, [selectedChannel]);

  const fetchMessages = async (channelId) => {
    try {
      const response = await fetch(`/api/server/${serverId}/channels/${channelId}/messages`);
      if (!response.ok) throw new Error('Failed to fetch messages');
      const data = await response.json();
      setMessages(data);
    } catch (error) {
      console.error('Error fetching messages:', error);
      setError('Error fetching messages: ' + error.message);
    }
  };

  const handleAddMenuItem = () => {
    setMenuItems([...menuItems, { label: menuItemLabel, message: menuItemMessage }]);
    setMenuItemLabel('');
    setMenuItemMessage('');
  };

  const handleCreateMenu = async () => {
    try {
      const response = await fetch(`/api/server/${serverId}/message/menu`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          channelId: selectedChannel,
          messageId: selectedMessage,
          menuType,
          menuItems,
        }),
      });
      if (!response.ok) throw new Error('Failed to create menu');
      alert('Menu created successfully');
    } catch (error) {
      console.error('Error creating menu:', error);
      setError('Error creating menu: ' + error.message);
    }
  };

  if (error) {
    return <div className="page"><p>{error}</p></div>;
  }

  return (
    <div className="section">
      <h2 onClick={() => document.getElementById('menuSection').classList.toggle('hidden')}>Create Menu</h2>
      <div id="menuSection" className="hidden">
        <label>
          Select Channel:
          <select value={selectedChannel} onChange={(e) => setSelectedChannel(e.target.value)}>
            {channels.map(channel => (
              <option key={channel.id} value={channel.id}>{channel.name}</option>
            ))}
          </select>
        </label>
        <label>
          Select Message:
          <select value={selectedMessage} onChange={(e) => setSelectedMessage(e.target.value)}>
            {messages.map(message => (
              <option key={message.id} value={message.id}>{message.content}</option>
            ))}
          </select>
        </label>
        <label>
          Menu Type:
          <select value={menuType} onChange={(e) => setMenuType(e.target.value)}>
            <option value="normal">Normal</option>
            <option value="dropdown">Dropdown</option>
          </select>
        </label>
        <div>
          <input
            type="text"
            value={menuItemLabel}
            onChange={(e) => setMenuItemLabel(e.target.value)}
            placeholder="Menu Item Label"
          />
          <input
            type="text"
            value={menuItemMessage}
            onChange={(e) => setMenuItemMessage(e.target.value)}
            placeholder="Menu Item Message"
          />
          <button onClick={handleAddMenuItem}>Add Menu Item</button>
        </div>
        <ul>
          {menuItems.map((item, index) => (
            <li key={index}>{item.label} - {item.message}</li>
          ))}
        </ul>
        <button onClick={handleCreateMenu}>Create Menu</button>
      </div>
    </div>
  );
}

export default Menu;
